import org.springframework.stereotype.Component;
import org.springframework.transaction.annotation.Transactional;

@Component
public class Test {
    @Transactional(rollbackFor = Exception.class)
    public void printA() {
        System.out.println();
    }

    public void printB() {
        System.out.println();
        printA();
        System.out.println();
    }

    public void printC() {
        printA();
    }

    public void printD() {
    	System.out.println();
    	System.out.println();
        printA();
    }
}
