package one.show;

import org.springframework.stereotype.Component;
import org.springframework.transaction.annotation.Transactional;

@Component
public class Test {
	@Async
	@Transactional(rollbackFor = Exception.class)
	public void printA() {
		System.out.println();
	}

	public void printB() {
		System.out.println();
		printA();
		System.out.println();
	}

	public void printC() {
		printA();
	}
}
